INSERT INTO `wp_options` VALUES (141,'_transient_dash_v2_88ae138922fe95674369b1cb3d215a2b','<div class=\"rss-widget\"><p><strong>RSS Error:</strong> WP HTTP Error: cURL error 28: Operation timed out after 10010 milliseconds with 212171 bytes received</p></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wptavern.com/open-survey-for-wordpress-theme-authors-on-json-files-and-block-themes?utm_source=rss&#038;utm_medium=rss&#038;utm_campaign=open-survey-for-wordpress-theme-authors-on-json-files-and-block-themes\'>WPTavern: Open Survey for WordPress Theme Authors on JSON Files and Block Themes</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/publishpress-adopts-organize-series-plugin?utm_source=rss&#038;utm_medium=rss&#038;utm_campaign=publishpress-adopts-organize-series-plugin\'>WPTavern: PublishPress Adopts Organize Series Plugin</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/refined-blog-a-curated-list-of-rss-feeds-for-software-engineering-blogs?utm_source=rss&#038;utm_medium=rss&#038;utm_campaign=refined-blog-a-curated-list-of-rss-feeds-for-software-engineering-blogs\'>WPTavern: Refined.blog: A Curated List of RSS Feeds for Software Engineering Blogs</a></li></ul></div>','no');
