INSERT INTO `wp_options` VALUES (238,'_transient_newses_about_plugin_info_shortbuild','O:8:\"stdClass\":20:{s:4:\"name\";s:10:\"ShortBuild\";s:4:\"slug\";s:10:\"shortbuild\";s:7:\"version\";s:5:\"1.6.9\";s:6:\"author\";s:51:\"<a href=\"https://managethemes.com\">managethemes</a>\";s:14:\"author_profile\";s:43:\"https://profiles.wordpress.org/managethemes\";s:12:\"contributors\";a:1:{s:12:\"managethemes\";a:3:{s:7:\"profile\";s:43:\"https://profiles.wordpress.org/managethemes\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/452ebbbef66fb0ad01ecbeaa8e145f5c?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:12:\"managethemes\";}}s:12:\"requires_php\";b:0;s:7:\"ratings\";a:5:{i:5;i:0;i:4;i:0;i:3;i:0;i:2;i:0;i:1;i:0;}s:11:\"num_ratings\";i:0;s:15:\"support_threads\";i:0;s:24:\"support_threads_resolved\";i:0;s:15:\"active_installs\";i:5000;s:8:\"homepage\";s:0:\"\";s:8:\"sections\";a:3:{s:11:\"description\";s:281:\"<p>ShortBuild, adds extra options for customizing home page of the Short Theme.</p>\n<p>The ShortBuild plugin features include:</p>\n<ul>\n<li>Beautiful ready-made homepage</li>\n<li>Predefined usefull sections</li>\n<li>Live content editing</li>\n<li>and many other features</li>\n</ul>\n\";s:9:\"changelog\";s:919:\"<h4>1.6.9</h4>\n<ul>\n<li>Fixed Issues.</li>\n</ul>\n<h4>1.6.8</h4>\n<ul>\n<li>Added Post Excerpt support.</li>\n</ul>\n<h4>1.6.7</h4>\n<ul>\n<li>Added child theme support.</li>\n</ul>\n<h4>1.6.6</h4>\n<ul>\n<li>Added theme support.</li>\n</ul>\n<h4>1.6.5</h4>\n<ul>\n<li>Added theme support.</li>\n</ul>\n<h4>1.6.4</h4>\n<ul>\n<li>Added theme support.</li>\n</ul>\n<h4>1.6.3</h4>\n<ul>\n<li>Fixed Slider image issues.</li>\n</ul>\n<h4>1.6.2</h4>\n<ul>\n<li>Fixed Slider section issue.</li>\n</ul>\n<h4>1.6.1</h4>\n<ul>\n<li>Fixed Service section issue.</li>\n</ul>\n<h4>1.6</h4>\n<ul>\n<li>Added child theme.</li>\n</ul>\n<h4>1.5</h4>\n<ul>\n<li>Added child theme.</li>\n</ul>\n<h4>1.4</h4>\n<ul>\n<li>Fixed Project issue.</li>\n</ul>\n<h4>1.3</h4>\n<ul>\n<li>Added Themes pages.</li>\n</ul>\n<h4>1.2</h4>\n<ul>\n<li>Added Theme Compatibility Setting.</li>\n</ul>\n<h4>1.1</h4>\n<ul>\n<li>First Commit.</li>\n</ul>\n<h4>1.0</h4>\n<ul>\n<li>First version of the plugin.</li>\n</ul>\n\";s:7:\"reviews\";s:0:\"\";}s:17:\"short_description\";s:76:\"ShortBuild, adds extra options for customizing home page of the Short Theme.\";s:13:\"download_link\";s:53:\"https://downloads.wordpress.org/plugin/shortbuild.zip\";s:11:\"screenshots\";a:0:{}s:8:\"versions\";a:0:{}s:7:\"banners\";a:0:{}s:5:\"icons\";a:1:{s:7:\"default\";s:54:\"https://s.w.org/plugins/geopattern-icon/shortbuild.svg\";}}','no');
