INSERT INTO `wp_options` VALUES (240,'_transient_newses_about_plugin_info_ansar-import','O:8:\"stdClass\":20:{s:4:\"name\";s:12:\"Ansar Import\";s:4:\"slug\";s:12:\"ansar-import\";s:7:\"version\";s:5:\"1.0.7\";s:6:\"author\";s:48:\"<a href=\"https://themeansar.com/\">Themeansar</a>\";s:14:\"author_profile\";s:41:\"https://profiles.wordpress.org/themeansar\";s:12:\"contributors\";a:1:{s:10:\"themeansar\";a:3:{s:7:\"profile\";s:41:\"https://profiles.wordpress.org/themeansar\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/19532e0eefdd8c88479e7935e95ff6b7?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:10:\"themeansar\";}}s:12:\"requires_php\";b:0;s:7:\"ratings\";a:5:{i:5;i:0;i:4;i:0;i:3;i:0;i:2;i:0;i:1;i:0;}s:11:\"num_ratings\";i:0;s:15:\"support_threads\";i:1;s:24:\"support_threads_resolved\";i:0;s:15:\"active_installs\";i:3000;s:8:\"homepage\";s:23:\"https://themeansar.com/\";s:8:\"sections\";a:5:{s:11:\"description\";s:261:\"<p>Import <a href=\"https://themeansar.com/\" rel=\"nofollow ugc\">Themeansar</a> official themes demo content</p>\n<h4>Requirements</h4>\n<ul>\n<li>WordPress 4.7 or later.</li>\n<li><a href=\"https://themeansar.com/\" rel=\"nofollow ugc\">Themeansar Themes</a></li>\n</ul>\n\";s:12:\"installation\";s:1358:\"<ol>\n<li>Install the plugin either via the WordPress.org plugin directory, or by uploading the files to your server (in the /wp-content/plugins/ directory).</li>\n<li>Activate the Themeansar Demo Importer plugin through the &#8216;Plugins&#8217; menu in WordPress.</li>\n</ol>\n<h4>Automatic installation</h4>\n<p>Automatic installation is the easiest option as WordPress handles the file transfers itself and you don’t need to leave your web browser. To do an automatic install of Ansar Import, log in to your WordPress dashboard, navigate to the Plugins menu and click Add New.</p>\n<p>In the search field type “Ansar Import” and click Search Plugins. Once you’ve found our demo importer plugin you can view details about it such as the the point release, rating and description. Most importantly of course, you can install it by simply clicking “Install Now”.</p>\n<h4>Manual installation</h4>\n<p>The manual installation method involves downloading our demo importer plugin and uploading it to your webserver via your favourite FTP application. The WordPress codex contains <a href=\"https://codex.wordpress.org/Managing_Plugins#Manual_Plugin_Installation\" rel=\"nofollow ugc\">instructions on how to do this here</a>.</p>\n<h4>Updating</h4>\n<p>Automatic updates should work like a charm; as always though, ensure you backup your site just in case.</p>\n\";s:3:\"faq\";s:335:\"\n<dt id=\'what%20is%20the%20plugin%20license%3F\'>\nWhat is the plugin license?\n</h4>\n<p>\n<ul>\n<li>This plugin is released under a GPL license.</li>\n</ul>\n</p>\n<dt id=\'what%20themes%20this%20plugin%20supports%3F\'>\nWhat themes this plugin supports?\n</h4>\n<p>\n<ul>\n<li>The plugin currently only supports Themeansar themes.</li>\n</ul>\n</p>\n\n\";s:9:\"changelog\";s:552:\"<h4>1.0.1 &#8211; 06-18-2020</h4>\n<ul>\n<li>Fixed issue</li>\n</ul>\n<h4>1.0.2 &#8211; 07-05-2020</h4>\n<ul>\n<li>Fixed issue</li>\n</ul>\n<h4>1.0.3 &#8211; 07-07-2020</h4>\n<ul>\n<li>Tag Update and fixed sanitize and escaping issue</li>\n</ul>\n<h4>1.0.4 &#8211; 23-07-2020</h4>\n<ul>\n<li>Added Cover Image.</li>\n</ul>\n<h4>1.0.5 &#8211; 23-07-2020</h4>\n<ul>\n<li>Changed Cover Image Size.</li>\n</ul>\n<h4>1.0.6 &#8211; 13-01-2021</h4>\n<ul>\n<li>Changed Cover Image Size.</li>\n</ul>\n<h4>1.0.7 &#8211; 28-06-2021</h4>\n<ul>\n<li>Changed ansar import strings.</li>\n</ul>\n\";s:7:\"reviews\";s:0:\"\";}s:17:\"short_description\";s:88:\"Import your demo content, widgets and theme settings with one click for Themeansar Theme\";s:13:\"download_link\";s:55:\"https://downloads.wordpress.org/plugin/ansar-import.zip\";s:11:\"screenshots\";a:0:{}s:8:\"versions\";a:0:{}s:7:\"banners\";a:2:{s:3:\"low\";s:67:\"https://ps.w.org/ansar-import/assets/banner-772x250.png?rev=2345288\";s:4:\"high\";b:0;}s:5:\"icons\";a:1:{s:2:\"1x\";s:65:\"https://ps.w.org/ansar-import/assets/icon-128X128.png?rev=2345223\";}}','no');
