INSERT INTO `wp_options` VALUES (244,'_transient_newses_about_plugin_info_woocommerce','O:8:\"stdClass\":20:{s:4:\"name\";s:11:\"WooCommerce\";s:4:\"slug\";s:11:\"woocommerce\";s:7:\"version\";s:5:\"5.5.2\";s:6:\"author\";s:48:\"<a href=\"https://woocommerce.com\">Automattic</a>\";s:14:\"author_profile\";s:40:\"https://profiles.wordpress.org/woothemes\";s:12:\"contributors\";a:14:{s:10:\"automattic\";a:3:{s:7:\"profile\";s:41:\"https://profiles.wordpress.org/automattic\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/687b3bf96c41800814e3b93766444283?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:10:\"automattic\";}s:10:\"mikejolley\";a:3:{s:7:\"profile\";s:41:\"https://profiles.wordpress.org/mikejolley\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/9b87f99f47e30735e1136c5141531fdf?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:11:\"Mike Jolley\";}s:11:\"jameskoster\";a:3:{s:7:\"profile\";s:42:\"https://profiles.wordpress.org/jameskoster\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/19a6a101c57df54bc1c40adcfba785d8?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:12:\"James Koster\";}s:14:\"claudiosanches\";a:3:{s:7:\"profile\";s:45:\"https://profiles.wordpress.org/claudiosanches\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/908480753c07509e76322dc17d305c8b?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:15:\"Claudio Sanches\";}s:13:\"rodrigosprimo\";a:3:{s:7:\"profile\";s:44:\"https://profiles.wordpress.org/rodrigosprimo\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/f2271ae6814a29d8cbf38ae2d0305a72?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:13:\"Rodrigo Primo\";}s:15:\"peterfabian1000\";a:3:{s:7:\"profile\";s:46:\"https://profiles.wordpress.org/peterfabian1000\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/be249b86dddbf495565a36196625d77a?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:15:\"peterfabian1000\";}s:7:\"vedjain\";a:3:{s:7:\"profile\";s:38:\"https://profiles.wordpress.org/vedjain\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/7a640eb2847d9a0a0d5e6af385e21881?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:13:\"Vedanshu Jain\";}s:8:\"jamosova\";a:3:{s:7:\"profile\";s:39:\"https://profiles.wordpress.org/jamosova\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/eb515897a71b0df82ccd2788548588c4?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:13:\"Julia Amosova\";}s:16:\"obliviousharmony\";a:3:{s:7:\"profile\";s:47:\"https://profiles.wordpress.org/obliviousharmony\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/185f31c1e5af65fee78506769ca13276?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:16:\"obliviousharmony\";}s:9:\"konamiman\";a:3:{s:7:\"profile\";s:40:\"https://profiles.wordpress.org/konamiman\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/b9a819e803dd87615c4aeb33192db976?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:9:\"konamiman\";}s:8:\"sadowski\";a:3:{s:7:\"profile\";s:39:\"https://profiles.wordpress.org/sadowski\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/766381c51d7072d1f48ee8878e2ce3af?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:8:\"sadowski\";}s:8:\"wpmuguru\";a:3:{s:7:\"profile\";s:39:\"https://profiles.wordpress.org/wpmuguru\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/7b8ff059b9a4504dfbaebd4dd190466e?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:11:\"Ron Rennick\";}s:5:\"royho\";a:3:{s:7:\"profile\";s:36:\"https://profiles.wordpress.org/royho\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/715b4097a10f874f8681b15608687f83?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:5:\"royho\";}s:13:\"barryhughes-1\";a:3:{s:7:\"profile\";s:44:\"https://profiles.wordpress.org/barryhughes-1\";s:6:\"avatar\";s:88:\"https://secure.gravatar.com/avatar/4d7aea8e7879e3ea257ddb4a99a300f1?s=96&d=monsterid&r=g\";s:12:\"display_name\";s:5:\"Barry\";}}s:12:\"requires_php\";s:3:\"7.0\";s:7:\"ratings\";a:5:{i:5;i:3133;i:4;i:155;i:3;i:80;i:2;i:81;i:1;i:306;}s:11:\"num_ratings\";i:3755;s:15:\"support_threads\";i:2312;s:24:\"support_threads_resolved\";i:1995;s:15:\"active_installs\";i:5000000;s:8:\"homepage\";s:24:\"https://woocommerce.com/\";s:8:\"sections\";a:6:{s:11:\"description\";s:11422:\"<p>WooCommerce is <a href=\"https://trends.builtwith.com/shop\" rel=\"nofollow ugc\">the world’s most popular</a> open-source eCommerce solution.</p>\n<p>Our core platform is free, flexible, and amplified by a global community. The freedom of open-source means you retain full ownership of your store’s content and data forever.</p>\n<p>Whether you’re launching a business, taking brick-and-mortar retail online, or developing sites for clients, use WooCommerce for a store that powerfully blends content and commerce.</p>\n<ul>\n<li><strong>Create beautiful, enticing storefronts</strong> with <a href=\"https://woocommerce.com/product-category/themes/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">themes</a> suited to your brand and industry.</li>\n<li><strong>Customize pages in minutes</strong> using modular <a href=\"https://docs.woocommerce.com/document/woocommerce-blocks/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">product blocks</a>.</li>\n<li>Showcase physical and digital goods, product variations, custom configurations, instant downloads, and affiliate items. <a href=\"https://woocommerce.com/products/woocommerce-bookings/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Bookings</a>, <a href=\"https://woocommerce.com/products/woocommerce-memberships/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">memberships</a>, <a href=\"https://woocommerce.com/products/woocommerce-subscriptions/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">subscriptions</a>, and <a href=\"https://woocommerce.com/products/dynamic-pricing/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">dynamic pricing</a> rules are only an extension away.</li>\n<li><strong>Rise to the top of search results</strong> by leveraging <a href=\"https://www.searchenginejournal.com/wordpress-best-cms-seo/\" rel=\"nofollow ugc\">WordPress’ SEO advantage</a>.</li>\n</ul>\n<p>Built-in tools and popular integrations help you efficiently manage your business operations. Many services are free to add with a single click via the optional <a href=\"https://docs.woocommerce.com/document/woocommerce-setup-wizard/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Setup Wizard</a>.</p>\n<ul>\n<li><strong>Choose how you want to get paid</strong>. Conveniently manage payments from the comfort of your store with <a href=\"https://woocommerce.com/payments/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">WooCommerce Payments</a> (U.S.-only). Securely accept cards, mobile wallets, bank transfers, and cash thanks to <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/payment-gateways/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">100+ payment gateways</a> – including <a href=\"https://woocommerce.com/products/stripe/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Stripe</a>, <a href=\"https://woocommerce.com/products/woocommerce-gateway-paypal-checkout/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">PayPal</a>, and <a href=\"https://woocommerce.com/products/square/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Square</a>.</li>\n<li><strong>Configure your shipping options</strong>. Print USPS labels right from your dashboard and even schedule a pickup with <a href=\"https://woocommerce.com/products/shipping/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">WooCommerce Shipping</a> (U.S.-only). Connect with <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/shipping-methods/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">well-known carriers</a> such as UPS, FedEx, and ShipStation – plus a wide variety of delivery, inventory, and fulfillment solutions for your locale.</li>\n<li><strong>Simplify sales tax</strong>. Add <a href=\"https://woocommerce.com/products/tax/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">WooCommerce Tax</a> or <a href=\"https://woocommerce.com/product-category/woocommerce-extensions/tax?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">similar integrated services</a> to make automated calculations a reality.</li>\n</ul>\n<h4>Grow your business, add features, and monitor your store on the go</h4>\n<p>WooCommerce means business. Keep tabs on the performance metrics most important to you with <a href=\"https://wordpress.org/plugins/woocommerce-admin/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\">WooCommerce Admin</a> – a powerful, customizable central dashboard for your store.</p>\n<p>Expand your audience across marketing and social channels with <a href=\"https://woocommerce.com/products/google-ads/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Google Ads</a>, <a href=\"https://woocommerce.com/products/hubspot-for-woocommerce/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">HubSpot</a>, <a href=\"https://woocommerce.com/products/mailchimp-for-woocommerce/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Mailchimp</a>, and <a href=\"https://woocommerce.com/products/facebook/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Facebook</a> integrations. You can always check out the in-dashboard <a href=\"https://docs.woocommerce.com/document/marketing-hub/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Marketing Hub</a> for fresh ideas and tips to help you succeed.</p>\n<p>Enhance store functionality with hundreds of free and paid extensions from the <a href=\"https://woocommerce.com/products/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">official WooCommerce Marketplace</a>. Our developers <a href=\"https://docs.woocommerce.com/document/marketplace-overview/#section-6?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">vet each new extension</a> and regularly review existing inventory to maintain Marketplace quality standards. We are actively <a href=\"https://docs.woocommerce.com/document/marketplace-overview/#section-2?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">looking for products that help store builders create successful stores</a>.</p>\n<p>Manage your store from anywhere with the free WooCommerce <a href=\"https://woocommerce.com/mobile/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">mobile app</a> (Android and iOS). Spoiler alert: Keep an ear out for the slightly addictive &#8220;cha-ching&#8221; notification sound each time you make a new sale!</p>\n<h4>Own and control your store data – forever</h4>\n<p>With WooCommerce, your data belongs to you. Always.</p>\n<p>If you opt to share <a href=\"https://woocommerce.com/usage-tracking/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">usage data</a> with us, you can feel confident knowing that it’s anonymized and kept secure. Choose to opt-out at any time without impacting your store.</p>\n<p>Unlike hosted eCommerce solutions, WooCommerce store data is future-proof; should you wish to migrate to a different platform, you’re free to export all your content and take your site wherever you choose. No restrictions.</p>\n<h4>Why developers choose (and love) WooCommerce</h4>\n<p>Developers can use WooCommerce to create, customize, and scale a store to meet a client’s exact specifications, making enhancements through extensions or custom solutions.</p>\n<ul>\n<li>Leverage <a href=\"https://docs.woocommerce.com/document/introduction-to-hooks-actions-and-filters/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">hooks and filters</a> to modify or create functionality.</li>\n<li>Integrate virtually any service using a robust <a href=\"https://docs.woocommerce.com/document/woocommerce-rest-api/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">REST API</a> and webhooks.</li>\n<li>Design and build custom content blocks with React.</li>\n<li><a href=\"https://docs.woocommerce.com/documentation/plugins/woocommerce/woocommerce-codex/extending/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Inspect and modify</a> any aspect of the core plugin code.</li>\n<li>Speed up development with a lightning-fast <a href=\"https://woocommerce.github.io/code-reference/classes/wc-cli-rest-command.html?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">CLI</a>.</li>\n</ul>\n<p>The core platform is tested rigorously and often, supported by a dedicated development team working across time zones. Comprehensive documentation is updated with each release, empowering you to build exactly the store required.</p>\n<h4>Be part of our growing international community</h4>\n<p>WooCommerce has a large, passionate community dedicated to helping merchants succeed, and it’s growing fast.</p>\n<p>There are <a href=\"https://woocommerce.com/meetups/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">WooCommerce Meetups</a> in locations around the world that you can attend for free and even get involved in running. These events are a great way to learn from others, share your expertise, and connect with like-minded folks.</p>\n<p>WooCommerce also has a regular presence at WordCamps across the globe – we’d love to meet you.</p>\n<h4>Contribute and translate</h4>\n<p>WooCommerce is developed and supported by Automattic, the creators of WordPress.com and Jetpack. We also have hundreds of independent contributors, and there’s always room for more. Head to the <a href=\"https://github.com/woocommerce/woocommerce?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">WooCommerce GitHub Repository</a> to find out how you can pitch in.</p>\n<p>WooCommerce is translated into multiple languages, including Danish, Ukrainian, and Persian. Help localize WooCommerce even further by adding your locale – visit <a href=\"https://translate.wordpress.org/projects/wp-plugins/woocommerce/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">translate.wordpress.org</a>.</p>\n\";s:12:\"installation\";s:2306:\"<h4>Minimum Requirements</h4>\n<ul>\n<li>PHP 7.2 or greater is recommended</li>\n<li>MySQL 5.6 or greater is recommended</li>\n</ul>\n<p>Visit the <a href=\"https://docs.woocommerce.com/document/server-requirements/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">WooCommerce server requirements documentation</a> for a detailed list of server requirements.</p>\n<h4>Automatic installation</h4>\n<p>Automatic installation is the easiest option &#8212; WordPress will handles the file transfer, and you won’t need to leave your web browser. To do an automatic install of WooCommerce, log in to your WordPress dashboard, navigate to the Plugins menu, and click “Add New.”</p>\n<p>In the search field type “WooCommerce,” then click “Search Plugins.” Once you’ve found us,  you can view details about it such as the point release, rating, and description. Most importantly of course, you can install it by! Click “Install Now,” and WordPress will take it from there.</p>\n<h4>Manual installation</h4>\n<p>Manual installation method requires downloading the WooCommerce plugin and uploading it to your web server via your favorite FTP application. The WordPress codex contains <a href=\"https://wordpress.org/support/article/managing-plugins/#manual-plugin-installation\">instructions on how to do this here</a>.</p>\n<h4>Updating</h4>\n<p>Automatic updates should work smoothly, but we still recommend you back up your site.</p>\n<p>If you encounter issues with the shop/category pages after an update, flush the permalinks by going to WordPress &gt; Settings &gt; Permalinks and hitting “Save.” That should return things to normal.</p>\n<h4>Sample data</h4>\n<p>WooCommerce comes with some sample data you can use to see how products look; import sample_products.xml via the <a href=\"https://wordpress.org/plugins/wordpress-importer/\">WordPress importer</a>. You can also use the core <a href=\"https://docs.woocommerce.com/document/product-csv-importer-exporter/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">CSV importer</a> or our <a href=\"https://woocommerce.com/products/product-csv-import-suite/?utm_source=wp%20org%20repo%20listing&amp;utm_content=3.6\" rel=\"nofollow ugc\">CSV Import Suite extension</a> to import sample_products.csv</p>\n\";s:3:\"faq\";s:7903:\"\n<dt id=\'where%20can%20i%20find%20woocommerce%20documentation%20and%20user%20guides%3F\'>\nWhere can I find WooCommerce documentation and user guides?\n</h4>\n<p>\n<p>For help setting up and configuring WooCommerce, please refer to <a href=\"https://docs.woocommerce.com/documentation/plugins/woocommerce/getting-started/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Getting Started</a> and the <a href=\"https://woocommerce.com/guides/new-store/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">New WooCommerce Store Owner Guide</a>.</p>\n<p>For extending or theming WooCommerce, see our <a href=\"https://docs.woocommerce.com/documentation/plugins/woocommerce/woocommerce-codex/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">codex</a>, as well as the <a href=\"https://docs.woocommerce.com/document/create-a-plugin/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Plugin Developer Handbook</a>.</p>\n</p>\n<dt id=\'where%20can%20i%20get%20help%20or%20talk%20to%20other%20users%20about%20woocommerce%20core%3F\'>\nWhere can I get help or talk to other users about WooCommerce Core?\n</h4>\n<p>\n<p>If you get stuck, you can ask for help in the <a href=\"https://wordpress.org/support/plugin/woocommerce/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\">WooCommerce Support Forum</a> by following <a href=\"https://wordpress.org/support/topic/guide-to-the-woocommerce-forum/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\">these guidelines</a>, reach out via the <a href=\"https://woocommerce.com/community-slack/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">WooCommerce Community Slack</a>, or post in the <a href=\"https://www.facebook.com/groups/advanced.woocommerce?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">WooCommerce Community group</a> on Facebook.</p>\n</p>\n<dt id=\'where%20can%20i%20get%20help%20for%20extensions%20i%20have%20purchased%20on%20woocommerce.com%3F\'>\nWhere can I get help for extensions I have purchased on WooCommerce.com?\n</h4>\n<p>\n<p>For assistance with paid extensions from the WooCommerce.com Marketplace: first, review our <a href=\"https://docs.woocommerce.com/document/woocommerce-self-service-guide/\" rel=\"nofollow ugc\">self-service troubleshooting guide</a>. If the problem persists, kindly log a support ticket via <a href=\"https://woocommerce.com/my-account/create-a-ticket/\" rel=\"nofollow ugc\">our helpdesk</a>. Our dedicated Happiness Engineers aim to respond within 24 hours.</p>\n</p>\n<dt id=\'i%E2%80%99m%20having%20trouble%20logging%20in%20to%20woocommerce.com%20%E2%80%93%20what%20now%3F\'>\nI’m having trouble logging in to WooCommerce.com – what now?\n</h4>\n<p>\n<p>First, troubleshoot common login issues using this helpful <a href=\"https://docs.woocommerce.com/document/log-into-woocommerce-com-with-wordpress-com/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">step-by-step guide</a>. Still not working? <a href=\"https://woocommerce.com/contact-us/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Get in touch with us</a>.</p>\n</p>\n<dt id=\'will%20woocommerce%20work%20with%20my%20theme%3F\'>\nWill WooCommerce work with my theme?\n</h4>\n<p>\n<p>Yes! WooCommerce will work with any theme but may require some additional styling. If you’re looking for a theme featuring deep WooCommerce integration, we recommend <a href=\"https://woocommerce.com/storefront/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Storefront</a>.</p>\n</p>\n<dt id=\'how%20do%20i%20update%20woocommerce%3F\'>\nHow do I update WooCommerce?\n</h4>\n<p>\n<p>We have a detailed guide on <a href=\"https://docs.woocommerce.com/document/how-to-update-woocommerce/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">How To Update WooCommerce</a>.</p>\n</p>\n<dt id=\'my%20site%20broke%20%E2%80%93%20what%20do%20i%20do%3F\'>\nMy site broke – what do I do?\n</h4>\n<p>\n<p>Start by diagnosing the issue using our helpful <a href=\"https://docs.woocommerce.com/documentation/get-help/troubleshooting-get-help/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">troubleshooting guide</a>.</p>\n<p>If you noticed the error after updating a theme or plugin, there might be compatibility issues between it and WooCommerce. If the issue appeared after updating WooCommerce, there could be a conflict between WooCommerce and an outdated theme or plugin.</p>\n<p>In both instances, we recommend running a conflict test using <a href=\"https://docs.woocommerce.com/document/troubleshooting-using-health-check/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Health Check</a> (which allows you to disable themes and plugins without affecting your visitors) or troubleshooting the issue using a <a href=\"https://docs.woocommerce.com/document/how-to-test-for-conflicts/#section-3?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">staging site</a>.</p>\n</p>\n<dt id=\'where%20can%20i%20report%20bugs%3F\'>\nWhere can I report bugs?\n</h4>\n<p>\n<p>Report bugs on the <a href=\"https://github.com/woocommerce/woocommerce/issues?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">WooCommerce GitHub repository</a>. You can also notify us via our support forum – be sure to search the forums to confirm that the error has not already been reported.</p>\n</p>\n<dt id=\'where%20can%20i%20request%20new%20features%2C%20themes%2C%20and%20extensions%3F\'>\nWhere can I request new features, themes, and extensions?\n</h4>\n<p>\n<p>Request new features and extensions and vote on existing suggestions on our official <a href=\"https://ideas.woocommerce.com/forums/133476-woocommerce?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">ideas board</a>. Our Product teams regularly review requests and consider them valuable for product planning.</p>\n</p>\n<dt id=\'woocommerce%20is%20awesome%21%20can%20i%20contribute%3F\'>\nWooCommerce is awesome! Can I contribute?\n</h4>\n<p>\n<p>Yes, you can! Join in on our <a href=\"https://github.com/woocommerce/woocommerce/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">GitHub repository</a> and follow the <a href=\"https://woocommerce.wordpress.com/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">development blog</a> to stay up-to-date with everything happening in the project.</p>\n</p>\n<dt id=\'where%20can%20i%20find%20rest%20api%20documentation%3F\'>\nWhere can I find REST API documentation?\n</h4>\n<p>\n<p>Extensive <a href=\"https://woocommerce.github.io/woocommerce-rest-api-docs/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">WooCommerce REST API Documentation</a> is available on GitHub.</p>\n</p>\n<dt id=\'my%20question%20is%20not%20listed%20here.%20where%20can%20i%20find%20more%20answers%3F\'>\nMy question is not listed here. Where can I find more answers?\n</h4>\n<p>\n<p>Check out <a href=\"https://docs.woocommerce.com/document/frequently-asked-questions/?utm_medium=referral&amp;utm_source=wordpress.org&amp;utm_campaign=wp_org_repo_listing\" rel=\"nofollow ugc\">Frequently Asked Questions</a> for more.</p>\n</p>\n\n\";s:9:\"changelog\";s:945:\"<h4>5.5.2 2021-07-22</h4>\n<ul>\n<li>Fix &#8211; Add a new option allowing product downloads to be served using redirects as a last resort. #30288</li>\n<li>Fix &#8211; Remove unnecessary seacrh related &#8216;where&#8217; clause added in the &#8216;post_clauses&#8217; hook handling. #30335</li>\n<li>Fix &#8211; Check before calling $screen method to make sure its not null. #30277</li>\n</ul>\n<p>**WooCommerce Admin &#8211; 2.4.4 &amp; 2.4.3 &amp; 2.4.2 **</p>\n<ul>\n<li>Fix &#8211; Fix homepage stock panel regression in 2.4.3. #7389</li>\n<li>Fix &#8211; Add a new low stock products endpoint to improve the performance. #7377</li>\n<li>Fix &#8211; Add lazy loading by checking panel open status. #7376</li>\n<li>Fix &#8211; Add cache-control header to low stock REST API response. #7364</li>\n</ul>\n<p><a href=\"https://raw.githubusercontent.com/woocommerce/woocommerce/trunk/changelog.txt\" rel=\"nofollow ugc\">See changelog for all versions</a>.</p>\n\";s:11:\"screenshots\";s:1035:\"<ol><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-1.jpg?rev=2366418\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-1.jpg?rev=2366418\" alt=\"\"></a></li><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-2.jpg?rev=2366418\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-2.jpg?rev=2366418\" alt=\"\"></a></li><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-3.jpg?rev=2366418\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-3.jpg?rev=2366418\" alt=\"\"></a></li><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-4.jpg?rev=2366418\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-4.jpg?rev=2366418\" alt=\"\"></a></li><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-5.jpg?rev=2366418\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-5.jpg?rev=2366418\" alt=\"\"></a></li><li><a href=\"https://ps.w.org/woocommerce/assets/screenshot-6.jpg?rev=2366418\"><img src=\"https://ps.w.org/woocommerce/assets/screenshot-6.jpg?rev=2366418\" alt=\"\"></a></li></ol>\";s:7:\"reviews\";s:14256:\"<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Elementor issues appear with the latest woocommerce update</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/misaghfar\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/c2ba45c5c2eefa984087e5bb328d7e9e?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/c2ba45c5c2eefa984087e5bb328d7e9e?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/misaghfar\" class=\"reviewer-name\">misaghfar</a> on <span class=\"review-date\">July 28, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Since the latest update the Elementor page builder is having issues and the WIDGETs\' PANEL will not load unless I Deactivate the Woocommerce plugin. This is a serious issue, hope you Woocommerce guys look in to it and come up with a proper solution soon</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Latest version causing fatal error</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"1 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"1\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/interskill\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/511b437d4c2e20d41a84233a74002325?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/511b437d4c2e20d41a84233a74002325?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/interskill\" class=\"reviewer-name\">Interskill</a> on <span class=\"review-date\">July 25, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">When saving the shop owner info a white screen gives a 500 error \n\nThis page isn\'t working at the moment</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Shopping Cart Error or Classic Widget Page View</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/abbas78\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/4d62b8d68b353ad7a0020cd3a5e3b13f?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/4d62b8d68b353ad7a0020cd3a5e3b13f?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/abbas78\" class=\"reviewer-name\">abbas78</a> on <span class=\"review-date\">July 25, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">In the latest version, there is a problem displaying the widget page in the admin panel</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Excelente</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/binhospbr\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/8525a991f0968d22b4a74c18cb4298e1?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/8525a991f0968d22b4a74c18cb4298e1?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/binhospbr\" class=\"reviewer-name\">binhospbr</a> on <span class=\"review-date\">July 22, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Faz com que nosso trabalho fique muito mais fácil!</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">The best Ecommerce plugin</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/bakhshispco\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/24dcaee304fa603a6f8f233a249a724e?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/24dcaee304fa603a6f8f233a249a724e?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/bakhshispco\" class=\"reviewer-name\">bakhshispco</a> on <span class=\"review-date\">July 17, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Number One Plugin For Ecommerce</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Simply excellent</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/hinddoc\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/b76dd618bb98e5e630204ab94e53856c?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/b76dd618bb98e5e630204ab94e53856c?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/hinddoc\" class=\"reviewer-name\">hinddoc</a> on <span class=\"review-date\">July 16, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">First-time reviewer here. This Deserves a 6 star.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Simple, diverse, great</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/herbsmaster\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/9370d2d78e3e62d27822505cf1f5b284?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/9370d2d78e3e62d27822505cf1f5b284?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/herbsmaster\" class=\"reviewer-name\">herbsmaster</a> on <span class=\"review-date\">July 15, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Try it for your business. You can\'t go wrong. It is very simple to use.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Stille no solution to filter out out of stock variations for variable products?!</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"2 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"2\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span><span class=\"star dashicons dashicons-star-empty\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/madfcat\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/1c0e4023e2107c07846fa2248a21fc9c?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/1c0e4023e2107c07846fa2248a21fc9c?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/madfcat\" class=\"reviewer-name\">madfcat</a> on <span class=\"review-date\">July 10, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Guys!\n\nAll in all the plugin is an amazing solution to transform your wordpress into a shop.\n\nBut, seriously, you just need to implement this important functionality: filter should not show out of stock variations of variable products.\n\nImagine you have a shoe store. User wants to buy only size 40 and still after filtering woocommerce shows all the items that have that variation even if it\'s out of stock.\n\nA lot of people with clothing, shoe and actually every store with variable products need this!!</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">Excellent shop plugin</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/vaclavek\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/8433240f337bdb305586c5f7b305b4f8?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/8433240f337bdb305586c5f7b305b4f8?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/vaclavek\" class=\"reviewer-name\">vaclavek</a> on <span class=\"review-date\">July 9, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">This is one of the best shopping solutions not only for the wp platform but at all. I have experience with different CMS systems but woocommerce really the best.</div>\n</div>\n<div class=\"review\">\n	<div class=\"review-head\">\n		<div class=\"reviewer-info\">\n			<div class=\"review-title-section\">\n				<h4 class=\"review-title\">excelent - if all plugins payed</h4>\n				<div class=\"star-rating\">\n				<div class=\"wporg-ratings\" aria-label=\"5 out of 5 stars\" data-title-template=\"%s out of 5 stars\" data-rating=\"5\" style=\"color:#ffb900;\"><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span><span class=\"star dashicons dashicons-star-filled\"></span></div>				</div>\n			</div>\n			<p class=\"reviewer\">\n				By <a href=\"https://profiles.wordpress.org/gitta2lu\"><img alt=\'\' src=\'https://secure.gravatar.com/avatar/8d5e268091ccb64324ea14a22f03c07d?s=16&#038;d=monsterid&#038;r=g\' srcset=\'https://secure.gravatar.com/avatar/8d5e268091ccb64324ea14a22f03c07d?s=32&#038;d=monsterid&#038;r=g 2x\' class=\'avatar avatar-16 photo\' height=\'16\' width=\'16\' loading=\'lazy\'/></a><a href=\"https://profiles.wordpress.org/gitta2lu\" class=\"reviewer-name\">gitta2lu</a> on <span class=\"review-date\">July 9, 2021</span>			</p>\n		</div>\n	</div>\n	<div class=\"review-body\">Nice tool, if you have spent lots of time, money and learnd e-commerce needs and benefits. You need to buy plugins to run your shop. So be aware woocommerce basic is free, sure. That\'s like buying a car with an engine but without wheels, oil,  fuel, key etc. You need money and time to run this tool. But if you spent money and time you maybe have a shop to sell things. \nHave fun \nGitta\n\n</div>\n</div>\n\";}s:17:\"short_description\";s:73:\"WooCommerce is the world’s most popular open-source eCommerce solution.\";s:13:\"download_link\";s:60:\"https://downloads.wordpress.org/plugin/woocommerce.5.5.2.zip\";s:11:\"screenshots\";a:6:{i:1;a:2:{s:3:\"src\";s:64:\"https://ps.w.org/woocommerce/assets/screenshot-1.jpg?rev=2366418\";s:7:\"caption\";b:0;}i:2;a:2:{s:3:\"src\";s:64:\"https://ps.w.org/woocommerce/assets/screenshot-2.jpg?rev=2366418\";s:7:\"caption\";b:0;}i:3;a:2:{s:3:\"src\";s:64:\"https://ps.w.org/woocommerce/assets/screenshot-3.jpg?rev=2366418\";s:7:\"caption\";b:0;}i:4;a:2:{s:3:\"src\";s:64:\"https://ps.w.org/woocomme